$(function () {


    $("a.top").click(function () {
        $("html,body").animate({
            "scrollTop": 0
        }, 1000, 'swing');
    });


    $('a.profile').click(function () {
        var service = $('#profile').offset().top;
        $('html, body').animate({
            scrollTop: service
        });
    });

    $('a.oiriginal').click(function () {
        var service = $('#oiriginal').offset().top;
        $('html, body').animate({
            scrollTop: service
        });
    });

});
